import { Button, Card, Popover } from "antd";
import {
  RetweetOutlined,
  HeartOutlined,
  MessageOutlined,
  EllipsisOutlined,
} from "@ant-design";
import PropTypes from "prop-types";
import { useSelector } from "react-redux";

const PostCard = ({ post }) => {
  const { me } = useSelector((state) => state.user);
  const id = me && me.id; // == me?.id <optional chaining>

  return (
    <>
      <div>
        <Card
          cover={post.Image[0] && <PostImages images={post.Images} />}
          actions={[
            // jsx에서 배열을 작성할 때는 key를 항상 작성
            <RetweetOutlined key="retweet"></RetweetOutlined>,
            <HeartOutlined key="heart"></HeartOutlined>,
            <MessageOutlined key="comment"></MessageOutlined>,
            <Popover
              key="more"
              content={
                <Button.Group>
                  {id && post.User.id === id ? (
                    <>
                      <Button>Update</Button>
                      <Button type="danger">Delete</Button>
                    </>
                  ) : (
                    <Button>신고</Button>
                  )}
                </Button.Group>
              }
            >
              <EllipsisOutlined></EllipsisOutlined>
            </Popover>,
          ]}
        ></Card>
      </div>
    </>
  );
};

PostCard.propTypes = {
  post: PropTypes.shape({
    id: PropTypes.number,
    User: PropTypes.object,
    content: PropTypes.string,
    createAt: PropTypes.object,
    Comments: PropTypes.arrayOf(PropTypes.object),
    Images: PropTypes.arrayOf(PropTypes.object),
  }).isRequired,
};

export default PostCard;
